package training.metofficeweather.data.loctionInformation;import com.fasterxml.jackson.annotation.JsonIgnoreProperties;import com.fasterxml.jackson.annotation.JsonProperty;import java.util.ArrayList;import java.util.stream.Stream;@JsonIgnoreProperties(ignoreUnknown = true)public class Location {    @JsonProperty(value="Location")        private ArrayList<LocationInformation> location;    public ArrayList<LocationInformation> getLocation() {        return location;    }    public void setLocation(ArrayList<LocationInformation> location) {        this.location = location;    }    public Stream<LocationInformation> streamLocationInformation() {        return location.stream();    }    @Override    public String toString() {        return "Location{" +                "location=" + location +                '}';    }}